import { FormControl } from '@angular/forms';
import { map, startWith } from 'rxjs';
export class AutocompleteHandler {
  constructor(values, control = new FormControl('')) {
    this.values = values;
    this.control = control;
  }

  init() {
    this.filteredValues = this.control.valueChanges.pipe(startWith(''), map(value => this._filter(value || '')));
  }

  _filter(value) {
    const filterValue = value.toLowerCase();
    return this.values.filter(option => option.toLowerCase().includes(filterValue));
  }

}
